mapper.insert:
INSERT INTO service
    (user_id, service_date ,service_number, service_radios, service_person, service_memo) VALUES
    (#{user_id}, #{service_date}, #{service_number}, #{service_radios}, #{service_person}, #{service_memo});

mapper.update:
UPDATE service SET service_check = 1
AND service_no IN
${item}

mapper.delete:
DELETE FROM service


        AND service_no IN

            ${item}

mapper.update:
UPDATE service SET service_check = 0
WHERE service_no NOT IN
${item}

mapper.select:
SELECT u.user_name, s.service_date, s.service_check
FROM service AS s INNER JOIN users AS u ON s.user_id = u.user_name
WHERE u.user_id = #{user_id}

mapper.insert:
INSERT INTO review
    (user_id, review_title, review_content, review_date, review_image) VALUES
    (#{user_id}, #{review_title}, #{review_content}, now(), #{review_image});
