mapper.select:
SELECT qna_no, qna_type, user_name, qna_title, qna_content, qna_date, qna_result, (SELECT count(*) FROM qna) as total_count
FROM qna


        AND qna_no = #{qna_no}


        AND qna_title LIKE '%' #{search} '%'



    LIMIT 10 OFFSET #{page}

mapper.update:
UPDATE qna SET
qna_type = #{qna_type}
,qna_title = #{qna_title}
,qna_content = #{qna_content}
WHERE qna_no = #{qna_no}
