mapper.insert:
INSERT INTO sponsor (
                        user_id
                        , spon_number
                        , spon_date
                        , spon_huwon
                        , spon_money
                        , spon_pay
                        , spon_open
                        , spon_content
                        , spon_tid
                    ) VALUES (
                        #{partner_user_id}
                        , #{partner_order_id}
                        , now()
                        , "일반 후원"
                        , #{total_amount}
                        , #{spon_pay}
                        , #{spon_open}
                        , #{spon_content}
                        , #{tid}
                    )

mapper.select:
SELECT u.user_name AS user_name
, s.spon_date AS spon_date
, s.spon_huwon AS spon_huwon
, s.spon_money AS spon_money
, s.spon_pay AS spon_pay
, s.spon_open AS spon_open
, s.spon_content AS spon_content
, (SELECT COUNT(*) FROM sponsor AS s INNER JOIN users AS u ON s.user_id = u.user_id) as total_count
FROM sponsor AS s INNER JOIN users AS u ON s.user_id = u.user_id


        u.user_name LIKE '%'||#{user_name}||'%'


        AND s.user_id LIKE '%'||#{user_id}||'%'


        AND s.spon_date > #{start_date}


        AND s.spon_date < #{end_date}


        AND s.spon_pay = #{spon_pay}


        AND s.spon_open = #{spon_open}


        AND s.spon_huwon = #{spon_huwon}


ORDER BY spon_date desc

    LIMIT 10 OFFSET #{page}
