mapper.select:
SELECT visit_no,user_id,elder_id,elder_no,visit_volume,visit_date,service_chk
FROM visit


        AND visit_no = #{visit_no}


        AND user_id = #{user_id}


        AND elder_id = #{elder_id}


        AND elder_no = #{elder_no}


        AND visit_volume = #{visit_volume}


        AND visit_date = #{visit_date}


        AND service_chk = #{service_chk}




    order by ${visit_no} desc

mapper.insert:
INSERT INTO visit
(user_id, elder_id,elder_no , visit_date,visit_volume,visit_time) values
(#{user_id}, #{elder_id}, #{elder_no}, #{visit_date}, #{visit_volume},#{visit_time}
)
